// Fork of "Shiny Circle" by phil. https://shadertoy.com/view/ltBXRc
// 2023-07-05 08:42:05

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

float variation(vec2 v1, vec2 v2, float strength, float speed) {
	return sin(
        dot(normalize(v1), normalize(v2)) * strength + iTime * speed
    ) / 100.0;
}

float sdRoundRect( in vec2 p, in vec2 b, in float r ) {
    vec2 q = abs(p)-b+r;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r;
}



vec3 paintCircle (vec2 uv, vec2 center, float rad, float width) {

    vec2 diff = center-uv;
    float len = length(diff);

    //len += variation(diff, vec2(0.0, 1.0), 5.0, 2.0);
    //len -= variation(diff, vec2(1.0, 0.0), 5.0, 2.0);

    float circle = smoothstep(rad-width, rad, len) - smoothstep(rad, rad+width, len);
    return vec3(circle);
}


vec3 paintRoundRect (vec2 uv, vec2 center, vec2 size, float rad, float width) {
    vec2 diff = center-uv;
    float len = sdRoundRect(diff, size, rad);

    //len += variation(diff, vec2(0.0, 1.0), 5.0, 2.0);
    //len -= variation(diff, vec2(1.0, 0.0), 5.0, 2.0);

    float circle = smoothstep(rad-width, rad, len) - smoothstep(rad, rad+width, len);
    return vec3(circle);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= 1.5;
    uv.x -= 0.25;

    vec3 color;
    float radius = 0.05;
    vec2 size = vec2(0.3);
    vec2 center = vec2(0.5);


    //paint color circle
    color = paintRoundRect(uv, center, size, radius, 0.1);

    //color with gradient
    vec2 v = rotate2d(iTime) * uv;
    color *= vec3(v.x, v.y, 0.7-v.y*v.x);

    //paint white circle
    color += paintRoundRect(uv, center, size, radius, 0.01);


	fragColor = vec4(color, 1.0);
}
