void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel1, uv/SIZE).rgb;

    vec2 gv = fract(fragCoord/SIZE) - 0.5;

    float d = length(gv);

    col *= smoothstep(.1, .0, d-.4);
    //vec3 mask = vec3(1.,.75,1.);
    //vec3 mask = vec3(1.*abs(sin(t+2.*Pi/3.)),1.*abs(sin(t+4.*Pi/3.)),1.*abs(sin(t+2.*Pi)));
    //col.rb = gv;
    //vec3 maskRed = vec3(white + col.r*0.7,white,white),1.)
    // Output to screen
    float white = (col.g + col.b)/0.75;
    fragColor = vec4(vec3(white + col.r*0.7,white,white),1.);
    //fragColor = vec4(col,1.0);
}

vec3 Avg(ivec2 p)
{
    float totalRed = 0. , totalGreen = 0., totalBlue = 0., temp, coef = 0.2, frange = 2.0;
    int range = 2;
    float div = (frange*2.+1.)*(frange*2.+1.) - 1.8;
    float divRed = 0., divGreen = 0., divBlue = 0.;
    float reward = 0.1;

    for (int x=-range; x<=range; x++)
    {
        for (int y=-range; y<=range; y++)
        {
            if (x==0 && y==0) continue;
            totalRed += texelFetch(iChannel1, p+ivec2(x,y), 0).r;
            totalGreen += texelFetch(iChannel1, p+ivec2(x,y), 0).g;
            totalBlue += texelFetch(iChannel1, p+ivec2(x,y), 0).b;
        }
    }

    if (totalRed >= totalGreen && totalRed > totalBlue)
    {
        totalRed = totalRed - totalGreen;
        totalGreen *= coef;
        divRed = reward;
    } 
    else if (totalGreen > totalRed && totalGreen >= totalBlue)
    {
        totalGreen = totalGreen - totalBlue;
        totalBlue *= coef;
        divGreen = reward;

    }
    else if (totalBlue >= totalRed && totalBlue > totalGreen)
    {
        totalBlue = totalBlue - totalRed;
        totalRed *= coef;
        divBlue = reward;
    }
    return vec3(totalRed/(div-divRed), totalGreen/(div-divGreen), totalBlue/(div-divBlue));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = vec4(0);

    if (iFrame < 200) //initialize
    {
        //col = vec4(vec3(texture(iChannel0, uv).r),1.);
        col = vec4(texture(iChannel0, uv).rgb,1.);
        //if (length(iMouse.xy/SIZE - fragCoord.xy) < BRUSH_SIZE)
        //col = vec4(0.,0.,1.,1.);

        //col = vec4(0);
    }
    else if (iFrame % SPEED !=0)
        col = texture(iChannel1, uv);
    else //alive!
    {
        float red = texelFetch(iChannel1, ivec2(fragCoord), 0).r;
        float green = texelFetch(iChannel1, ivec2(fragCoord), 0).g;
        float blue = texelFetch(iChannel1, ivec2(fragCoord), 0).b;

        float re = 0.8, ofs = 0.3;

        if (red == green && green == blue)
            if (red < re)
                col.rgb = Avg(ivec2(fragCoord));
            else 
                col.rgb = vec3(0.1);
        else
        {
            if (red < re)
            {
                if (blue < ofs)
                    col.r = Avg(ivec2(fragCoord)).r;
                else 
                    col.r = 0.;
            }
            else 
                col.r = red-0.5;

            if (green < re)
            {
                if (red < ofs)
                    col.g = Avg(ivec2(fragCoord)).g;
                else 
                    col.g = 0.;
            }
            else 
                col.g = green-0.5;

            if (blue < re)
            {
                if (green < ofs)
                    col.b = Avg(ivec2(fragCoord)).b;
                else
                    col.b = 0.;
            }
            else 
                col.b = blue-0.5;
        }
    }
    if (iMouse.z > 0.5 && length(iMouse.xy/SIZE - fragCoord.xy) < BRUSH_SIZE) col = vec4(0.5,0.,0.,1.);
    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 old = texture(iChannel1, uv);
    vec4 new = texture(iChannel0, uv);

    fragColor = mix(old, new, 1.);
}
