void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(texture(iChannel0, uv+texture(iChannel1, uv).r).b,texture(iChannel0, uv+texture(iChannel1, uv).r).b,1.);
    if(abs(uv.x-0.5)>0.2) {
        col = texture(iChannel2, uv*5. + .2).rgb;
    } else {
        col = (col + texture(iChannel2, uv*5.+texture(iChannel1, uv).r).rgb)/2.;
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float scrollLeft = .2*texture(iChannel0, .05*(uv+.209*iTime)).r;
    uv.x = 1.-uv.x;
    float scrollRight = .2*texture(iChannel0, .05*(uv+.2*iTime)).r;
    fragColor = vec4(scrollLeft+scrollRight,.0,.0,.0);
}
