vec3 palette(float t) {

    vec3 a = vec3(0.648, 0.428, 0.708);
    vec3 b = vec3(-0.269, 0.170, 0.531);
    vec3 c = vec3(5.900, 3.719, 4.866);
    vec3 d = vec3(0.617, 4.532, 5.648);
    return a + b*cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float Time = iTime * 0.2;

    vec2 uv = (fragCoord / iResolution.xy) - 0.5;
    uv.x *= iResolution.y/iResolution.x;
    uv *= 5.0;

    float p = uv.y - (sin(uv.x) * cos(Time)) * sin(Time * 0.2) * 20.0;

    vec3 col = palette(p*0.005 + Time * 0.1);

    p = fract(abs(p));
    col *= p + 0.2;

    fragColor = vec4(col ,1.0);
}
