void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = ((fragCoord * 2.0 - iResolution.xy) / (iResolution.y*2.))+.5;
    float pct = 0.0;

    pct = pow(distance(st,vec2(sin(iTime)*.5+.5)), distance(st,vec2(0.420,0.700)));
    pct = smoothstep((sin(iTime*.6)*.38+.5), 0.90, pct);

    vec3 color = (vec3(0.820,0.805,0.404)*pct)+vec3(0.565,0.000,0.022);

	fragColor = vec4( color, 1.0 );
}
