void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y*2.5;
    vec3 col=vec3(0);
    float x=uv.x, y=uv.y, t=iTime/1.9, c=1./x/y;
    for(float i=-10.; i<10.; i=i+.02)
  { 
    float h=sin(c*i*4.+t)*1.2, v=sin(c*i*3.);
    if ((x+.01)>h && (x-.01)<h && (y+.02)>v && (y-.02)<v)
    col = vec3(.2,1,.2);
  }
    fragColor = vec4(col,1.);
}
