// https://en.wikipedia.org/wiki/YUV
// https://en.wikipedia.org/wiki/YCbCr

mat3 RGB2YUV=mat3(
    0.299, -0.168736, 0.5,
    0.587, -0.331264, -0.418688,
    0.114, 0.5,              -0.081312
);

mat3 YUV2RGB=mat3(
    1.0,      1.0,              1.0,
    0.0,      -0.344136, 1.772,
    1.402, -0.7136,      0.0
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 inputRgb=texture(iChannel0,uv).rgb;
    vec3 yuv=RGB2YUV * inputRgb;
    if(uv.x<0.5){
      yuv.y*=-1.0;
      // yuv.y=-0.5;
    }else{
      // yuv.y=+0.5;
    }
    if(uv.y<0.5){
      yuv.z*=-1.0;
      // yuv.z=-0.5;
    }else{
      // yuv.z=+0.5;
    }
    vec3 outputRgb= YUV2RGB * yuv;
    fragColor.rgb=outputRgb;
    fragColor.a=1.0;
}
