float sdCircle( vec2 uv, vec2 p, float r )
{
    float d = length(uv-p);
    float c = step(d,r);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;
    float d1 = sdCircle(uv,vec2(0.0,0.0),0.7);
    float d2 = sdCircle(uv,vec2(0.24,0.2),0.13);
    float d3 = sdCircle(uv,vec2(-.24,0.2),0.13);

    float s1 = sdCircle(uv,vec2(0.0,0.02),0.5);
    float s2 = sdCircle(uv,vec2(0.0,0.1),0.5);
    float s = s1-s2;

    float d = d1 - d2 - d3 -s;

    fragColor = vec4(d,d,0.0,1.0);
}
