float LinearRgbToLuminance(vec3 linearRgb)
{
    return dot(linearRgb, vec3(0.2126729f,  0.7151522f, 0.0721750f));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 pic = texture(iChannel0, uv);

    float picLuminance = LinearRgbToLuminance(pic.rgb);
    fragColor = vec4(picLuminance, picLuminance, picLuminance, 1.);
}
