#define op 100
vec2 mul(vec2 u, vec2 v){
    float otv1=(u.x*v.x-u.y*v.y);
    float otv2=(u.x*v.y+v.x*u.y);
    return vec2(otv1,otv2);
}
vec2 square(vec2 n){
    return vec2(n.x*n.x - n.y * n.y,2.0*n.x*n.y);
}

float res(vec2 x){
    int i = 0;
    vec2 z = vec2(0.0,0.0);
    for(int i = 1; i < op;i++){
        z=square(z)+x;
        if(z.x*z.x + z.y*z.y>4.0){
            return float(i);
        }
    }
    return 0.0;
}

float f(float x){
    return pow(2.71828, - 2.0*x * x);
}
float f1(float x){
    return 1.0 / (1.0+pow(2.7182818284,-x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    //fragColor = vec4(col,1.0);
    vec2 stco = vec2(4.0 * fragCoord.x / iResolution.x  - 2.0,4.0 * fragCoord.y / iResolution.y-2.0);
    float col = res(vec2(stco.x,stco.y)) * sin(iTime);
    fragColor = vec4(1.0/col,f(col),f1(col),1.0)-texture(iChannel1,uv);
}
