float angleTarget = 0.403;
float targetDelta = 0.01;
float timeScale = 0.25;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    uv.y = 1.0 - uv.y;
    vec2 source = vec2(0.5, -0.17320508075);
    float angle = atan(uv.x - source.x, uv.y - source.y);
    float target = mod(iTime * timeScale, angleTarget * 2.0) - angleTarget;
    float delta = target - angle;
    if(delta > 0.0) fragColor = vec4(0, targetDelta / (targetDelta + delta), 0, 0);
    else fragColor = vec4(0);
    if(-angleTarget > angle || angle > angleTarget) fragColor = vec4(0);
}
