void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord - .5*iResolution.xy ) / iResolution.y;

    // Green background
    vec3 color = vec3(0.0, 0.4, 0.2);

    // Yellow rhombus
    float d = abs(uv.x * .64) + abs(uv.y);
    if (d < 0.45
   ) {
        color = vec3(1.0, 0.84, 0.0);
    }

     // Blue circle
    float r = length(uv);
    if (r < 0.30) {
        color = vec3(0.004, 0.1, 1.0);

        // White band without text
        float angle = radians(18.);
        mat2 rotationMatrix = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

        uv *= rotationMatrix;


        if (abs(uv.y) < .025 && abs(uv.x) < .30) {
            color = vec3(1.);
        }

    }

	fragColor = vec4(color,1.);
}
