vec2 r(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}


float g(vec2 co){
   vec2 i = floor(co);
   vec2 f = fract(co);

   vec2 u  =  f*f*(3.0-2.0*f);

   float a = dot(r(i + vec2(0.0)), f - vec2(0.0));
   float b = dot(r(i + vec2(1.0, 0.0)), f - vec2(1.0, 0.0));
   float c = dot(r(i + vec2(0.0, 1.0)), f - vec2(0.0, 1.0));
   float d = dot(r(i + vec2(1.0, 1.0)), f - vec2(1.0, 1.0));

   return mix(mix(a,b,u.x),mix(c,d,u.x),u.y);

}

vec2 FBM2(vec2 c ){
    float a = 0.5;
    float f = 1.0;
    float t = 0.0;
    float n = 0.0;

    int o = 3;
    float p = 0.5;
    float l = 2.0;

    for(int i = 0; i < o; i++){
      float no = g(c * f);

      t += no * a;
      n += a;

      a *= p;
      f *= l;

    }


    return vec2( t /= n);

}

float FBM(vec2 c)
{
    float a = 0.5;
    float f = 1.0;
    float t = 0.0;
    float n = 0.0;

    int o = 3;
    float p = 0.5;
    float l = 2.0;

    for(int i = 0; i < o; i++){
        float no = g(c * f + iTime * 0.5);
        t += a * no;
        n += a;

        f *= l;
        a *= p;
    }

    t /= n;


    return t;
} 


float wrapFBM(vec2 c){
    vec2 o1 = FBM2(c);
    vec2 o2 = FBM2(c + o1 * 4.0);

    float ns = FBM(c + o2  * 4.0);
    return ns;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 10.0 / 10.0;
    vec4 color = vec4(1.0);
    color.rgb = vec3(vec2(uv.x * 100.0,uv.y * 10.0),10.0) * vec3(wrapFBM(uv));

    // Output to screen
    fragColor = color;
}
