#define NUM_CIRC 8.

float sdPie(vec2 p, float a, float r){
  vec2 c = vec2(sin(a),cos(a));
  p.x = abs(p.x);
  float l = length(p) - r;
  float m = length(p-c*clamp(dot(p,c),0.,r));
  return max(l,m*sign(c.y*p.x-c.x*p.y));
}

float pacman(vec2 uv, float rot) {
    mat2 rotMat = mat2(cos(rot),-sin(rot), sin(rot), cos(rot));
    return smoothstep(0.,.055,sdPie(uv * rotMat,3.145*.75,.9));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.y;

    uv*=NUM_CIRC;

    vec2 fv = fract(uv)*2. -1.;
    vec2 iv = floor(uv);
    vec3 col = vec3(1.);
    float dir = 1.;
    float adj = 0.;
    if(mod(iv.x,2.)==0.){
      dir = -1.;
      adj = 1.;
    }
    float time = iTime*1.5;
    col -= pacman(fv, -(time + 6.29*(iv.x*.25 + adj*.25 + iv.y*.25))*dir);
    fragColor = vec4(col,1.);
}
