//x is the real part and y is the imaginary part

//function from https://www.shadertoy.com/view/tlByWG
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
vec2 add(vec2 c1,vec2 c2){
    return vec2(
    c1.x+c2.x,
    c1.y+c2.y
    );
}
vec2 mul(vec2 c1,vec2 c2){
    return vec2(
    c1.x*c2.x - c1.y*c2.y,
    c1.x*c2.y + c1.y*c2.x
    );
}

int calcJulia(vec2 complexNumber,vec2 constC, int max_iterations){
    int iterations = 0;
    vec2 zn = complexNumber;
    float b = 0.0;
    while(iterations <= max_iterations && b <= 2.0){
        zn = mul(zn,zn);
        zn = add(zn, constC);
        iterations +=1;
        b = length(zn);
    }
    return iterations;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int max_iter = 100;

    float min_zoom = 1.;
    float max_zoom = 2.;
    float period = 10.;
    float phaseShift = 3.14159265359;
    float h1 = log(max_zoom);
    float h0 = (log(min_zoom)-h1)*0.5;

    float tz =h0 *(cos(phaseShift+((2.0 * 3.14159265359* iTime)/period))+1.0)+h1;

    float zoom = pow( 2.71828, tz );

    vec2 start_point0 = vec2(0., 0.);
    vec2 start_point1 = vec2(-0.46012222, 0.5702860);
    vec2 originOffset = vec2(iResolution.x/2., iResolution.y/2.); 
    vec2 c = ( fragCoord + originOffset - iResolution.xy ) / iResolution.y;
    c = zoom * c + start_point0;

    //calc amount of iterations needed to succed the threshold
    int iter = calcJulia(c,vec2(0.5*sin((2.0 * 3.14159265359* iTime)/40.) ,0.7*cos(((2.0 * 3.14159265359* iTime)/30.))), max_iter);

    float hue = float(iter)/float(max_iter);
    float saturation = 1.0;
    float brightness = iter<max_iter?1.0:0.0;
    vec3 col = hsv2rgb(vec3(hue,saturation,brightness));

    // Output to screen
    fragColor = vec4(col,1.0);
}
