void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;


    vec4 img = texture(iChannel0, uv);
    vec4 img1 = texture(iChannel0, uv+0.0002);
    float diff = abs(length(img1.xyz) - length(img.xyz));
    vec3 col = vec3(diff);
    if (diff < 0.007){
        col = vec3(0);
    }
    else{
        diff *= 1.2;

    }

    col *= 120.;
    if (diff > 0.4){
        col = vec3(2.);
    }else{
    }

    col *= vec3(0.5,0.,0.);
    //col *= img.xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
