#define PIXW (1./iResolution.y)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  fragColor=vec4(0.);

  vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;
  //uv split
  p=fract((fragCoord / iResolution.y)*4.);
  p-=0.5;
  p*=2.;
  //p*=2.;
  //p=fract(p);

  //绘制一个圆
  if(length(p)<0.4)
      fragColor=vec4(1.);



  float d=length(p);

  //消除锯齿，绘制一个圆
  d=smoothstep(0.6,0.64,d);
  fragColor=vec4(1.0-d);

  //极坐标绘制一个圆
  float angle=(atan(-p.y,-p.x)/3.14+1.)/2.0;
  fragColor=mix(vec4(0.),fragColor,angle);




  angle-=iTime*.2;

  angle-=floor(angle);
  float ct=angle-iTime*0.7;
  ct-=floor(ct);

  float len=length(p);
  //look interesting, hard to understand why clearly---
  fragColor=vec4(1.0-smoothstep(-0.05,20.0*PIXW,d-0.3*sin(angle*10.*3.14)*.5+0.02));

  fragColor=mix(vec4(.7,.3,.8,1.)*fragColor,vec4(.5,.3,.2,1.)*fragColor,ct);
