void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
	vec2 uv = fragCoord.xy / 0.5 * 2.0 / iResolution.xy - 2.0;
	float t = iTime;

	float v = 0.;
	v += sin((uv.x + t) * 6.) * .5 + sin((uv.y + t) * 6.) * .5 + sin((uv.x + uv.y + t) * 6.) * .5;
	v = abs(v);
	v = smoothstep(.1, .8, v);
	v = pow(v, 32.);

    vec3 col = vec3(0.);
	float d = length(uv);
	col = vec3(1.54 - pow(d, .55), 1.535 - pow(d, .55), 1.53 - pow(d, .55));
	fragColor = vec4(col * v, 1.);
}
