float box2(vec2 p,vec2 b){
    p = abs(p)-b;
    return length(max(vec2(0.),p))+min(0.,max(p.x,p.y));
}
vec3 erot(vec3 p,vec3 ax,float t){return mix(dot(ax,p)*ax,p,cos(t))+cross(ax,p)*sin(t);}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);
    vec3 p,d=normalize(vec3(uv,1.));

    for(float i=0.,e=0.,g=0.;i++<99.;){

        p=d*g;
          p.z+=5.+iTime;  p.xy-=1.5;
        p =asin(sin(p/2.)*.9)*2.;


        float sc=1.;
        for(float j=0.;j++<6.;){

          p.xy =abs(p.xy)-.5;
          p.xy *=1.3;
          p = erot(p,normalize(vec3(0.,1.,1.)),-.785);
          sc*=1.3;

        }

        float h = box2(erot(p,vec3(0.,0.,1.),floor(iTime*140./60.*2.+length(uv*uv)+pow(dot(sin(uv*950.),cos(uv.yx*850.)),1.)*.5)).xy,vec2(.1));
        h = min(h,box2(p.xz,vec2(.1)));
        h = min(h,box2(p.yz,vec2(.1)));
        h/=sc;
        g+=e=max(.001,abs(h));
        col+=(.5+.5*cos(vec3(.1,.2,.5)*i+floor(iTime*140./60.+length(uv*uv)+pow(dot(sin(uv*950.),cos(uv.yx*850.)),1.)*.2)))*.15/exp((.5+p.z*.5)*i*i*e);
    }
    fragColor = vec4(col,1.0);
}
