void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 tex = texture(iChannel0,fragCoord/iResolution.xy);
    fragColor = vec4(tex.xyz/tex.w,1);
}

//Thanks to Inigo Quilez for the PRNG and trigophers for helping me make this progressive


int seed = 2;
float check_radius = 300.0;
vec3 rgb;
int frand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float rand(void) { return float(frand())/32767.0; }
vec3 point;
vec3 lightpos = vec3(10,25,50.0);
float hash1( uint n ) {
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uint(0x7fffffffU))/float(0x7fffffff);
}


float sde;
vec3 pos;
float maxl = 10000.0;


vec3 sphrand(){
    point = vec3(2.0);
    while(length(point)>=1.0){
        point = vec3(rand()*2.0-1.0,rand()*2.0-1.0,rand()*2.0-1.0);
        if (rand()<length(point)){
            point = vec3(2.0);
        }
    }
    return point;
}

float plane_sde(vec3 ray, vec3 nor, float planey, vec3 col){
    if (dot(ray,nor)+planey < sde){
        sde = dot(ray,nor)+planey;
        rgb = col;
    }
    return dot(ray,nor)+planey;
}


float sphere_sde(vec3 ray, vec3 position, float radius, vec3 col){
    if (length(position-ray)-radius < sde){
        sde = length(position-ray)-radius;
        rgb = col;
    }
    return length(position-ray)-radius;
}


float calc_sdf(vec3 ray){
    sde = maxl;
    sphere_sde(ray,vec3 (-10.0,5.0,80.0),15.0, vec3(1,0,0));
    plane_sde(ray,vec3(0,1,0),10.0, vec3(1,1,1));
    plane_sde(ray,vec3(1,0,0),51.0, vec3(0,0,1));
    plane_sde(ray,vec3(0,-1,0),61.0, vec3(1,1,1));
    plane_sde(ray,vec3(0,0,-1),111.0, vec3(1,1,1));
    plane_sde(ray,vec3(-1,0,0),51.0, vec3(1,0,0));
    sphere_sde(ray,vec3 (10.0,2.0,80.0),10.0, vec3(0,1,1));
    return sde;
}


vec3 calcNormal( in vec3 p ) // for function f(p)
{
    const float eps = 0.0001; // or some other value
    const vec2 h = vec2(eps,0);
    return normalize( vec3(calc_sdf(p+h.xyy) - calc_sdf(p-h.xyy),
                           calc_sdf(p+h.yxy) - calc_sdf(p-h.yxy),
                           calc_sdf(p+h.yyx) - calc_sdf(p-h.yyx) ) );
}


vec4 raycol(vec3 orig, vec3 dir, float ml){
    float len = 0.0;
    sde = ml;
    float steps = 0.0;
    pos = orig;

    while (len < ml && sde > 0.1){
        sde = calc_sdf(pos);
        pos += dir*sde;
        len += sde;
        steps += 1.0;
    }
    return vec4(pos,steps);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 newfrag = vec2(fragCoord.x+iResolution.x/2.0,fragCoord.y+iResolution.y/2.0); 
    uvec2 p = uvec2((newfrag-.5*iResolution.xy)/iResolution.y*1000.0);
    seed = int(round(hash1( p.x + 1920U*p.y + (1920U*1080U))*100.0*float(iFrame+1)));
    vec3 pos = vec3(0.0,20.0,-10.0);
    vec4 data = texture(iChannel0,fragCoord/iResolution.xy);
    vec4 fincol;
    vec3 direction = normalize(vec3(uv+sphrand().xy/400.0,1));
    vec4 o;
    float steps;
    o = raycol(pos,direction,500.0);
    if (sde <= 0.1){
        steps =o.w;
        vec3 inter = o.xyz;
        vec3 ocol = rgb;
        vec3 colour = vec3(0);
        for (int i = 0; i<10; i++){
            vec3 offset = sphrand();
            offset *= check_radius;
            lightpos = vec3(10,50,40)+sphrand()*10.0;
            offset += lightpos;
            vec3 hit = raycol(lightpos,normalize(offset-lightpos),length(offset-lightpos)).xyz;
            vec3 trgb = rgb;
            vec3 hitt = raycol(hit+calcNormal(hit),normalize(inter-hit),length(hit-inter)).xyz;
            if (length(hitt-inter)<2.0){
                colour += (1.0/(length(hitt-hit)/100.0))*trgb*dot(normalize(hit-inter),calcNormal(inter))*ocol;
            }

        }
        colour /= 10.0;
        fincol += vec4(colour,1.0);
    } else {
        fincol += vec4(0.0,0.0,0.0,1.0);
    }
    data += fincol;
    fragColor = data;
}
