void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec4 buf = texture(iChannel0, fragCoord / iResolution.xy);
  int idx = int(floor(buf.a * float(PALETTE_SIZE)));
  fragColor = vec4(colors[idx], 1.0);
}

//#define SIMPLE_PALETTE

float gray(vec3 color) {
  return (min(color.r, min(color.g, color.b)) + max(color.r, max(color.g, color.b))) * 0.5;
}

#ifdef SIMPLE_PALETTE
int getPalette(vec3 col) {
  float g = clamp(gray(col), 0.0, 0.99);
  return int(floor(g * float(PALETTE_SIZE)));
}
#else
int getPalette(vec3 col) {
  // comparasion
  float minDist = 9e9;
  int newCol;

  for (int i = 0; i < PALETTE_SIZE; i++) {
    vec3 compareCol = colors[i];

    vec3 distVec = abs(col - compareCol);
    float dist = (distVec.r + distVec.g + distVec.b) / 3.0;

    if (dist < minDist) {
      newCol = i;
      minDist = dist;
    }
  }

  return newCol;
}
#endif

float fsin(float a) {
  return sin(a) * 0.5 + 0.5;
}

vec3 hs(vec3 c, float s){
  vec3 m=vec3(cos(s),s=sin(s)*.5774,-s);
  return c*mat3(m+=(1.-m.x)/3.,m.zxy,m.yzx);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / iResolution.xy;

  vec4 tex = texture(iChannel1, uv);
  vec4 tex2 = texture(iChannel0, uv);

  float angle = iTime * 0.8 + uv.y * PI + uv.x * PI;
  float amp = 2.0 + fsin(iTime * 1.7 + uv.y * PI + uv.x * PI) * 2.0;

  vec4 left = texture(iChannel0,     uv + dir(angle + PI,       amp) / iResolution.xy);
  vec4 top = texture(iChannel0,      uv + dir(angle + -PI*0.5,  amp) / iResolution.xy);
  vec4 topleft = texture(iChannel0,  uv + dir(angle + -PI*0.75, amp) / iResolution.xy);
  vec4 topright = texture(iChannel0, uv + dir(angle + -PI*0.25, amp) / iResolution.xy);

  vec3 adjTex = tex.rgb +
    (left.rgb     * 7./16. +
    topright.rgb * 3./16. +
    top.rgb      * 5./16. +
    topleft.rgb  * 1./16.);

  int colIdx = getPalette(adjTex);
  vec3 col = colors[colIdx];
  vec3 error = abs(tex.rgb - col);

  fragColor = vec4(abs(error / (col + 0.02) / hs(1.0 - tex2.rgb, iTime * 0.2 - uv.y - uv.x) * 0.3), float(colIdx) / float(PALETTE_SIZE));
}
