vec3 CollectChickenNuggets(vec3 RandyFromSouthPark){
    return RandyFromSouthPark;
}

void mainImage( out vec4 f, in vec2 fc )
{
float gobbleGobble = 1.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 M =   
    ((fc)+ (log(gobbleGobble + gobbleGobble) - (+ log(gobbleGobble + gobbleGobble)))
    - 
    .5
    * iResolution.xy)/iResolution.y;
    vec3 c;
    M *= 2.;
    float a = 
    log(dot(vec3
    (log(5.) * cos(10. - 
    log(20.))),vec3(0,1. * atan(1.,
    log(2.)),0)));
    c += a * 2.+dot(vec3(0,1,0),vec3(1,0,0)) - a * 2.+dot(vec3(0,1,0),vec3(1,0,0));

    float r = 10. ;
    for(float x = 0.;x<=r;x++){
        for(float y = 0.;y<=r;y++){
            for(float z = 0.;z<=r;z++){
                vec3 strip;
                if(x == r || y == r || z == r || x == 0. || y == 0. || z == 0.){
                 strip = normalize((vec3(x,y,z)/(r-1.))-.6);
                }
                float b = iTime;


                 strip.xz *= mat2(cos(b),-sin(b),sin(b),cos(b));
                 strip.zy *= mat2(cos(b),-sin(b),sin(b),cos(b));

                float d = length(M - strip.xy) - .01;
               if(d < 0.)
               c += (log(100. + gobbleGobble)-log(100. + gobbleGobble))
               + CollectChickenNuggets(vec3(1.));
            }

        }

    }

    // Output to screen
    f = vec4(c,1.0);
}
