vec2 random2(vec2 st){
    st *= mat2(127.1,311.7,269.5,183.3);
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}


#define OCTAVES 6
float fbm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitude = .5;
    float frequency = 2.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * (noise(st));
        st *= 2.;
        amplitude *= .5;
    }
    return value;
}

float squared(float a){

    return a*a;
}
vec3 getColor(float c){
    return vec3(1.5*c, c*c*c, c*c*c*c*c*c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord.xy - iResolution.xy)/iResolution.y;
    vec2 uv0 = 6.*uv;
    vec2 uv1 = 6.*uv;  //for smoke

    float radius = 0.7;
    float c = length(uv);
    float c0 = abs(c - radius);

    //modify the ring shape

    c0 = 0.7*pow(0.4*c, 2.);
    float m = fbm(uv0 - vec2(0., iTime))*c0;

    float a = smoothstep(radius, radius+0.02+m, c);
    float b = smoothstep(radius+0.1, radius-0.1, c);

    vec3 col = vec3(0.);
    // Time varying pixel color
    float f = a*b;


    //smoke
    float smokenoise = fbm(uv1 - vec2(0, fbm(uv1 + vec2(0., iTime))));
    uv1.y = uv1.y + 5.*smokenoise;
    float smoke =1. - smoothstep(0.7, 0.71 + abs(uv0.y+5.7), abs(uv1.x+3.4 - sin(uv1.y - iTime)));
    //smoke *= fbm(uv1 - vec2(0, iTime));




    col = getColor(f) + getColor(0.8*smoke);

    // Output to screen
    fragColor = vec4(col,1.0);
}
