#define S(i) if(int(N)==i)a=

// C = fragColor
// F = fragCoord
void mainImage( out vec4 C, in vec2 F )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = F/iResolution.xy;

    float d, Y=u.y, N = u.x*6.;

    // "V"
    d = Y + sin(N*3.)*.8;
    bool a = d > 1. && d < 1.1;
    //a = sin(Y-cos(1.6+u.x*18.)*.8+.47) > .996;

    // "I"
    S(1) N > 1.45 && N < 1.55;

    // "C"
    d = length(vec2(N*.4-1.11,Y-.5));
    S(2) N < 2.6 && d < .3 && d > .25;

    // "T"
    S(3) Y > .76 || (N > 3.45 && N < 3.55);

    // "O"
    d = N*N-9.*N+20.5+Y*Y-Y;
    S(4) d < .09 && d > .04;

    // "R"
    d = length(vec2(N*.169-1.1,Y-.48));
    S(5) (N > 5.2 && N < 5.35 && Y < .65) || (Y>.5 && Y<.7 && d < .22 && d > .195);

    // Time varying pixel color or black background
    C = a && Y > .2 && Y < .8 ? cos(iTime-u.yxyx) : vec4(0);
}
