#define Tmin 0.0
#define Tmax 900.0
#define Raymarch_Time 200
#define PRESSICON 0.001
#define AA 1
#define PI 3.141592653579
#define NUM_OCTAVES 5

vec2 fixuv(in vec2 st,float multi){
    return multi*(2.0*st-iResolution.xy)/min(iResolution.x,iResolution.y);
}


vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}


float noise(vec2 st) {//return -0.6~0.6
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

float snoise(vec2 v) {//return -1.0~1.0
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
                     + i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}
float fbm ( in vec2 _st) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

vec3 random3(vec3 c) {
        float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
        vec3 r;
        r.z = fract(512.0*j);
        j *= .125;
        r.x = fract(512.0*j);
        j *= .125;
        r.y = fract(512.0*j);
        return r-0.5;
}

/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
        /* 1. find current tetrahedron T and it's four vertices */
        /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
        /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

        /* calculate s and x */
        vec3 s = floor(p + dot(p, vec3(F3, F3, F3)));
        vec3 x = p - s + dot(s, vec3(G3, G3, G3));

        /* calculate i1 and i2 */
        vec3 e = step(vec3(0,0,0), x - x.yzx);
        vec3 i1 = e*(1.0 - e.zxy);
        vec3 i2 = 1.0 - e.zxy*(1.0 - e);

        /* x1, x2, x3 */
        vec3 x1 = x - i1 + G3;
        vec3 x2 = x - i2 + 2.0*G3;
        vec3 x3 = x - 1.0 + 3.0*G3;

        /* 2. find four surflets and store them in d */
        vec4 w, d;

        /* calculate surflet weights */
        w.x = dot(x, x);
        w.y = dot(x1, x1);
        w.z = dot(x2, x2);
        w.w = dot(x3, x3);

        /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
        w = max(0.6 - w, 0.0);

        /* calculate surflet components */
        d.x = dot(random3(s), x);
        d.y = dot(random3(s + i1), x1);
        d.z = dot(random3(s + i2), x2);
        d.w = dot(random3(s + 1.0), x3);

        /* multiply d by w^4 */
        w *= w;
        w *= w;
        d *= w;

        /* 3. return the sum of the four surflets */
        return dot(d, vec4(52.0, 52.0, 52.0, 52.0));
}


float noise_sum_abs(vec2 p,float f)
{
    p = p * 2.0;
    f += 1.0000 * abs(snoise(p)); p = 2.0 * p;
    f += 0.5000 * abs(snoise(p)); p = 2.0 * p;
    f += 0.2500 * abs(snoise(p)); p = 2.0 * p;
    f += 0.1250 * abs(snoise(p)); p = 2.0 * p;
    f += 0.0625 * abs(snoise(p)); p = 2.0 * p;

    return f;
}



float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float hillheight(vec3 location){
    float noisehill=simplex3d(location);
    float distancede=0.0;
    float zz=location.z;
    float xx=location.x;
    float yy=location.y;
    vec2 st=location.xz;
    float a=1.0;

    mat3 rot = mat3(1.0,0.0,0.0,
                0.0,cos(0.5), sin(0.5),
                0.0,-sin(0.5), cos(0.50));
    mat2 rot2d = mat2(
                cos(0.1), sin(0.1),
                -sin(0.1), cos(0.10));
    float desertground=0.0;
    float desertwave=0.0;
    for(int i=0;i<1;i++){
        st=rot2d*st*0.7+vec2(1.75,1.36);
        desertwave+=0.5*a*(3.0+5.0*noise(st))*abs(mod(0.01*st.x,1.0)-0.5);
        a*=0.5;

    }
    float desertall=desertwave;
    float desertheight=yy-desertall;
    return 0.9*desertheight;

}

float plotcloud(vec3 location){
    vec2 st= vec2(location.x,location.z);
    float fluence=fbm(st);
    float dis;
    fluence=pow(fluence,1.0);
    if(location.y>70.0){
        dis= Tmax;
    }
    else{
        dis=70.0-0.009*fluence-location.y;
    }

    return dis;
}

float map(in vec3 p){
    vec3 locatio=vec3(p.x,p.y-4.0,p.z);
    float texture=hillheight(p);
    return texture;
}

float raymarch(in vec3 ro,in vec3 rd){
    float t=Tmin;
    for(int i=0;i<Raymarch_Time&&t<Tmax;i++){
        vec3 p=ro+t*rd;
        float d=map(p);
        if(d<PRESSICON){
            break;
        }
        t+=d;
    }
    return t;
}

float raymarchcloud(in vec3 ro,in vec3 rd){
    float t=Tmin;
    for(int i=0;i<Raymarch_Time&&t<Tmax;i++){
        vec3 p=ro+t*rd;
        float d=plotcloud(p);
        if(d<PRESSICON){
            break;
        }
        t+=d;
    }
    return t;
}


vec3 calcNormal( in vec3  p ) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h) + 
                      k.yyx*map( p + k.yyx*h) + 
                      k.yxy*map( p + k.yxy*h) + 
                      k.xxx*map( p + k.xxx*h) );
}

mat3 camera(vec3 tar,vec3 cam,float tr){
    vec3 z=normalize(tar-cam);
    vec3 cp=vec3(sin(tr),cos(tr),0.0);
    vec3 x=normalize(cross(cp,z));
    vec3 y=cross(z,x);
    mat3 came=mat3(x,y,z);
    return came;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float w )
{float res = 1.0;
    float ph = 1e20;
    float t = mint;
    for( int i=0; i<256 && t<maxt; i++ )
    {
        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, d/(w*max(0.0,t-y)) );
        ph = h;
        t += h;
    }
    return res;
}

vec3 render(vec2 uv){
    vec3 color=vec3(0.0);
    float an=iTime*0.0009;
    float r=400.0;
    float h =-hillheight(vec3(r*sin(an),0.0,r*cos(an)));
    vec3 pos=vec3(r*sin(an),h,r*cos(an));
    vec3 ro =vec3(r*sin(an),h+0.5,r*cos(an));
    mat3 came=camera(vec3(r*sin(an+0.01),h+0.5,r*cos(an+0.01)),ro,0.0);
    vec3 rd=normalize(came*(vec3(uv,2.5)));
    float t=raymarch(ro,rd);

    if(t<Tmax){
        vec3 po=ro+t*rd;
        vec3 n=calcNormal(po);
        vec3 light=vec3(2.0,4.0,0.0);
        float phemisp=clamp(1.0*dot(normalize(light-po),n),0.0,1.0);
        float shadow=softshadow(po,normalize(light-po),Tmin,Tmax,0.06);
        //phemisp *= shadow;

        float amd=0.3+0.5*dot(n,vec3(0.0,1.0,0.0));
        //vec3(1.0,0.5,0.1)

        color=amd*vec3(0.4)+phemisp*vec3(1.0,0.471,0.12);
    }
    else{
        //float tcloud=raymarchcloud(ro,rd);
        vec3 skytop=vec3(0.236,0.467,0.975);
        vec3 skybottom=vec3(0.5,0.5,0.6);
        color=1.0*mix(skybottom,skytop,0.7+rd.y*rd.y);
        float cloudheight1=9.0;
        vec3 cloudposition=ro+(cloudheight1-ro.y)/rd.y*rd;
        color=mix(color,vec3(0.8),0.9*clamp(0.1,0.9,fbm((cloudposition.xz)*0.038+0.1*iTime)));
        /*
        if(tcloud<Tmax){
            color=vec3(0.8);
        }
*/

    }
    return color;

}


void mainImage(out vec4 fragColor,in vec2 fragCoord){
     vec3 color=vec3(0.0);
    for(int m=0;m<AA;m++){
        for(int n=0;n<AA;n++){
            vec2 offset=2.0*(vec2(float(m),float(n))/float(AA)-0.5);
            vec2 uv_ori_cube=fixuv(fragCoord+offset,5.0);
            color+=render(uv_ori_cube);
        }
    }
    // vec2 uv_ori_cube=fixuv(fragCoord,5.0);
    // color+=render(uv_ori_cube);
     fragColor = vec4(color/float(AA*AA),1.0);
    //fragColor = vec4(color,1.0);
}
