void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy/iResolution.y) * 2. - 1.;
    float t = iTime;
    float shape = mod(t, 8.);
    float r = length(p);

    if(shape < 2.)
    {
        // quadrado
        if(abs(p.x) < 0.5 && abs(p.y) < 0.5)
            fragColor = vec4(1, 1, 1, 1);
        else
            fragColor = vec4(0, 0, 0, 0);
    }
    else if(shape < 4.)
    {
        // circulo
        if(r < 0.5)
            fragColor = vec4(1, 1, 1, 1);
        else
            fragColor = vec4(0, 0, 0, 0);
    }
    else if(shape < 6.)
    {
        // triangulo
        if(p.x > 0.)
        {
            if(p.y > -p.x + 1.)
                fragColor = vec4(1, 1, 1, 1);
            else
                fragColor = vec4(0, 0, 0, 0);
        }
        else
        {
            if(p.y < p.x + 1.)
                fragColor = vec4(1, 1, 1, 1);
            else
                fragColor = vec4(0, 0, 0, 0);
        }
    }
    else
    {
        // retorna para quadrado
        if(abs(p.x) < 0.5 && abs(p.y) < 0.5)
            fragColor = vec4(1, 1, 1, 1);
        else
            fragColor = vec4(0, 0, 0, 0);
    }
}
