#define PI 3.141592653589793
float random (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}
float random (in float x) {
    return fract(sin(x)*1e4);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.);

    uv.x *= 200.;
    uv.y *= 20.;

    uv.x += (floor(sin(uv.y*PI))*2.+1.)*(sin(floor(iTime/1.5)))*iTime*100.*random(floor(uv.y+1.));

    vec2 ipos = floor(uv);
    vec2 fpos = fract(uv);

    float dim = clamp(0.01,0.99,(sin(floor(iTime/1.))+1.)*random(floor(uv.y+1.)));
    float pct = step(dim,random(ipos))*smoothstep(0.1,0.3,fpos.y)*smoothstep(1.,.7,fpos.y);

    col = pct*vec3(random(uv.y),random(uv.y),random(uv.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
