#define R iResolution


float C(vec2 uv, float pct) {
   return smoothstep(0.+pct, 0.4 + pct, 
   length(vec2(uv.x  , uv.y ))) 
   - smoothstep(0.4 + pct, 0.5 + pct, 
   length(vec2(uv.x  , uv.y  )));

}



float Circle2(vec2 uv, float pct) {
   return smoothstep(0., 0.1, 
   length(vec2(uv.x, uv.y ))) 
   - smoothstep(0.1, 0.7 + pct / 20., 
   length(vec2(uv.x, uv.y)));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*R.xy)/R.y;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0.2,0.5,0.));
    vec3 col1 = vec3(0.8, 0.2, 0.2);

    vec3 s;

    for (float f = 0.; f < 70.; f++) {
        s +=   C(vec2(uv.x + f/20. * sin(iTime), uv.y ), fract(f)  ) *  col * vec3(0.8 * f, 0.2 * f, 0.2);
    }


    vec3 t = vec3(Circle2(uv, 0.4 ));   
    fragColor = vec4(s / (t - col) + col  ,1.0);
}
