#define PixelSize 32.0
#define Brightness 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 i = ceil(fragCoord / PixelSize);

    float r = mod(i.x + i.y, 2.0);

    vec3 c = vec3(r) + Brightness;

    /*
    // Square colors
    if(r < 1.0)
        c = vec3(1., 0., 0.); // red
    else
        c = vec3(0., 1., 0.); // green

    c += Brightness; // add brightness
    */

    fragColor = vec4(c, 1.0);
}
