#define t iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float BandMultiplier = 129.0;
   float BandPower = 0.5;
   float BandOffset = 0.512;
   float BandWidth = 0.95;
   float BandWidth1 = 0.05;
   float Time = (sin(1.0*t)*0.1)+2.0;

   bool Switch = true;

   vec2 uv = fragCoord/iResolution.xy;
   vec2 uv2 = (fragCoord * 2.0 -iResolution.xy) / iResolution.y;


/*
    if (Switch == true)
    {
    BandWidth = 0.1;
    };
    */

  // bands

   //creating "k" variable and setting it with vertical coordinate from uv coordinates
   float k = uv.y;
   float l = uv.y;


   k = k-1.0;

   k = (abs(k)-BandOffset);

   k = BandMultiplier*k*Time;

   k = pow(k,BandPower);

   k = fract(k);

   k = smoothstep(BandWidth,0.4,k);

   k = abs(k-1.0);

   k = (step(BandOffset,abs(uv.y-1.0)))*k;


   l = l-1.0;

   l = (abs(l)-BandOffset);

   l = BandMultiplier*l*Time;

   l = pow(l,BandPower);

   l = fract(l);

   l = smoothstep(BandWidth1,0.4,l);

   l = abs(l-1.0);

   l = (step(BandOffset,abs(uv.y-1.0)))*l;



   // circle

    float d = length(uv2);

    d = smoothstep(0.1, 0.15, d - 0.5);

    d = 1.0 - d;

    d = d * uv.y;
    d = pow(d,0.4);

    float e = d*(smoothstep(0.49,0.5,uv.y));




   //float a = k+l;
   float a = ((l+k)*d)+e;
   //float a = e;

   fragColor = vec4(a-0.1, a-0.45, 0, 0);
}
