#define SZ 20.

#define ANIM_SPEED .15

//https://www.shadertoy.com/view/4djSRW
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float timeOffset=round(iTime/ANIM_SPEED);
    vec3 noise=hash33(vec3(fragCoord, timeOffset));

    vec2 coord=iResolution.xy*.5-fragCoord;
    coord+=(noise.xy-.5)*.3;

    //blue
    fragColor=vec4(.07, .3, .7, 1.);
    //small lines
    vec2 a=smoothstep(.07, .0, abs(.5-fract(coord/SZ)));
    fragColor+=.25*(a.x+a.y);
    //big lines
    vec2 b=smoothstep(.01, .0, abs(.5-fract(coord/(SZ*5.))));
    fragColor+=.5*(b.x+b.y);

    //composite
    vec2 uv=fragCoord/iResolution.xy;
    vec4 col=texture(iChannel0, uv);
    fragColor=mix(fragColor, col, smoothstep(.5, .6, length(col.rgb-vec3(.1, .9, .2))));

    //more noise
    fragColor+=(noise.x-.5)*.03;

    //add vignette
    //https://www.shadertoy.com/view/lsKSWR
    uv*=1.-uv.yx;
    float vig=uv.x*uv.y*22.;
    fragColor*=pow(vig, .17);
}
