mat2 Rot(float rad){
    return mat2(cos(rad),sin(rad),-sin(rad),cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=4.;
    p.yz*=Rot(iTime);
    p.xy*=Rot(.01);

    for(j=0.;j++<4.;)
        p=abs(abs(p)-.04)-.02,
        d=min(max(p.y,p.z),p.x),
        d-=dot(p,p)/2e4;

    e+=d;

}o+=vec4(sqrt(p),1)*8./i;
}
