vec3 palette( in float t )
{
    vec3 a = vec3(-0.692,0.428,0.568);
    vec3 b = vec3(-0.762,-0.462,0.648);
    vec3 c = vec3(0.178,1.199,0.178);
    vec3 d = vec3(-0.692,0.428,0.568);
    return a + b*cos( 6.28318*(c*t+d) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) /iResolution.y;
    vec2 uv0 = uv;
    vec3 finalcolour = vec3(0.0);
    for(float i = 0.0;i<3.0;i++){
    uv = fract(uv *(1.5))-0.5;


    float d = length(uv)*exp(-length(uv0+iMouse.x/10000.0));
    vec3 col = palette(length(uv0) + iTime*i*0.4);
    d = sin(d * 8. +iTime)/8.;
    d = abs(d);
    //d = (i/100.0)/d;
    d = smoothstep(0.0,0.25,d);
    d = (0.025)/d;
    //col *= d;
    finalcolour += col *(d);
    }
    fragColor = vec4(finalcolour,1.0);
}
