#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*0.5+0.5)

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1.*1.5));  
    for(float i=0.,s,e,g=1.;
        ++i<60./*80.*/;
        O.zxy+=.04*abs(cos(d+log(s)*.3))*exp(-1.5*0.7*i*i*e)
     //   O.xyz+=.04*abs(cos(d+log(s)*.3))*exp(-1.5*0.7*i*i*e)
    ) 
    {
        p=g*d;
      //  p.z-=mix(.5,10.,step(.5,fract(iTime*.1)));
        p.z-=1.5*(1.+0.2*sin(iTime*0.2));

        p=R(p,normalize(vec3(1,2,3)),iTime*.2);
        q=p;
        s=1.5;
        for(int j=0;j++< 8 /*12*/; )
            p=sign(p)*(1.2-abs(p-1.2)),

            //  Fractal 42  => Fractal 44 
        //    p=p*(e=8./clamp(dot(p,p),.5,5.))+q  ,                 s*=e;
          p=p*(e=8./clamp(dot(p,p),.5*0.75,5.*1.125))+q*0. -vec3(0.3,8.*1.5,0.3) , s*=e;

        g+=e=length(p)/s +(1e-4)*0.;
    }
}
