// JULIA SET ~ from iq

#define AA 2

F calc( v2 p, F time ){
    F  ltime = 0.5-0.5*cos(time*0.06);
    F  zoom  = 0.5;
    v2 cen = v2( 0.2655,0.301 ) + zoom*0.8*cos(4.0+2.0*ltime);
    v2 c   = v2( -0.745, 0.186 ) - 0.045*zoom*(1.0-ltime*0.5);
    p = (2.0*p-iR.xy)/iR.y;
    v2 z  = cen + (p-cen)*zoom;

    v2 dz = v2( 1.0, 0.0 ); // Scalers

    for( I i=0; i<256; i++ ){

        // HOW IS THIS POSSIBLE?

        // Shader Masters Please help!!!

        dz = 2.0*v2( z.x*dz.x - z.y*dz.y, z.x*dz.y + z.y*dz.x ); // This works
        //dz = 2.0*v2( z.x*dz.x, z.y*dz.x );                     // This does not

        // Why are there differing results between
        // multiplying a variable by 0.0
        // vs. multiplying a variable by scaler dz.y if dz = v2(1.0,0.0)?


        // ANSWER, by FabriceNeyret2 :

        // dz = v2( 1.0, 0.0 ) only at the beginning of the first iteration. 
        // Then it changes.

        z  = v2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y ) + c;
        if( D(z,z)>200.0 ) B;
    }
    F d = Q( D(z,z)/D(dz,dz) )*log(D(z,z));
    R Q( C( (150.0/zoom)*d, 0.0, 1.0 ) );
}

V mainImage( out v4 col, in v2 xy ){
    F scol = 0.0;
    scol += calc( xy, iTime );
    col = v4( v3(scol), 1.0 );
}
