/*
by Dom Mandy in 2023
*/
void mainImage(out vec4 o, vec2 i) {
    vec2 I = iResolution.xy;

    vec3 N, n, F,
         // ray
         R = vec3(0, 0, 1),
         // eye
         E = vec3(1.2 * (i + i - I) / min(I.x, I.y), -1),
         // light
         L = vec3(.7);

    // animation
    float k, D, f, d = 8.,
          t = mix(iTime / d, -iMouse.x / 200., max(0., sign(iMouse.z))),
          r = atan(R.z, R.x) - t,
          e = atan(E.z, E.x) - t,
          l = atan(L.z, L.x) - t;
    R.xz = vec2(cos(r), sin(r));
    E.xz = vec2(cos(e), sin(e)) * length(E.xz);
    L.xz = vec2(cos(l), sin(l)) * .7;

    // surface normals
    for(; k++ < 256.;) {
        N = normalize(texelFetch(iChannel0, ivec2(k, 8), 0).rgb - .5);
        N *= sign(f = dot(N, R));
        F = N - E - R * dot(N - E, N) / abs(f);
        if ((D = dot(F, F)) < d) d = D, n = N;
    }

    // lighting
    float O = max(0., dot(L, n)) + max(0., dot(reflect(L, n), -R));
    o.rgb = .7 * sqrt(O + O * .1 * vec3(8, 3, 9) * cos(atan(n.z, n.x) - vec3(0, 2, 4)));
}


