#define GRID 32.0

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float px = 2.0 / iResolution.y;
    vec2 uv0 = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 uv1 = mat2(cos(PI / 6.0), sin(PI / 6.0), -sin(PI / 6.0), cos(PI / 6.0)) * uv0;
    vec2 uv2 = mat2(cos(PI / 3.0), sin(PI / 3.0), -sin(PI / 3.0), cos(PI / 3.0)) * uv0;

    const int N = 3;
    vec2 uvs[] = vec2[](uv0, uv1, uv2);

    float hash;
    for (int i = 0; i < N; i++) {
        vec2 uv = uvs[i];
        uv.y += 0.1 * iTime;

        vec2 id = floor(uv * GRID) + vec2(42 * i, -69 * i);
        uv = mod(uv, 1.0 / GRID);

        float h = hash12(id);
        hash = intBitsToFloat(floatBitsToInt(hash) ^ floatBitsToInt(h));
    }

    vec3 color = hash31(hash);

    fragColor = vec4(color, 1.0);
}

#define PI 3.1415927

float hash12(vec2 p) {
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec3 hash31(float p) {
   vec3 p3 = fract(vec3(p) * vec3(.192, .594, .137));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}
