#define chanfro(a)  dot(cos(a + vec2(0, -11.)), q.xy)
#define wmod(p, w) (mod(p, w) - w/2.)
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t      iTime
#define PI     3.1415



void mainImage(out vec4 o, vec2 u) {
    vec3 R = iResolution, q,
         D = (vec3(u, R.y) - R/2.) / R.y;

    float d, s, ss, f, i, j,
          e = 1e-2;          

    for(o *= 0.; i++ < 44. ; d += max(ss, e / 5.)) {
        q = d * D;
        q.z -= 1.5;

        q.xz *= rot(cos(t) * .4);
        q.xy *= rot(sin(t) * .3);


        ss = length(q) - 1.;
        /*
        j = 4.;
        while(j < 32.) 
            q *= j * 1.8,
            s = dot(sin(q), vec3(8)),
            ss += .01 * abs(s) / j,
            j += j;
        */

        ss = length(q) - .5;
        j = 4.;
        vec3 k = q;
        while(j < 32.) 
            k *= j * .5 + cos(t) * .5,
            //q *= j * 2.8,
            s = dot(sin(k), vec3(1)) / j,
            ss = min(ss, length(q - vec3(s, 0, 0)) - .6),
            //ss += .01 * abs(s) / j,
            j += j;

        ss = abs(ss) - .001,
        f = 1. - ss / e, 

        f > -.5
            ? o += f / d * i / 400., f
            : o.g += .004;
    }
}
