void mainImage( out vec4 O, in vec2 F) {
    vec3 R = iResolution;
    float m=.2,h,i=iTime,l,z=9.,j;
    for(;j<4.&&l>=.0;
        i+=2.0944,
        j+=.9,
        h=clamp(.5+.5*(l-z)/1.,0.,1.),
        m=min(m,mix(l,z,h)-1.*h*(1.-h)),
        z=l
    )
        l=length((F+F-R.xy)/R.y-vec2(.4)*mat2(cos(i), sin(i), -sin(i), cos(i)))-.2+.1*sin(i),
        O=l<.0?vec4(1.+l*5.,1.,1.,1.):vec4(9.,5.,9.,1.)*m;
}
