void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvVectorFromCenter = uv - vec2(0.5, 0.5+(0.05*cos(iTime))); 

    vec3 col = vec3(mod(iTime*0.1+length(uvVectorFromCenter.x), 0.3), 
    0.6, 
    mod(iTime*0.05+sin(length(uvVectorFromCenter.y)), 0.32));

    float edge = (0.2f+sin(iTime)*0.02) + (abs(sin(iTime))*0.15)*
    sin(atan(uvVectorFromCenter.y, uvVectorFromCenter.x)*10000.0 + 10.0*uvVectorFromCenter.x + 100.0 * cos(iTime));

    col *= smoothstep(edge, edge+0.05, length(uvVectorFromCenter));

    float trunkEdge = 0.015 + sin(iTime)*0.001;
    col *= 1.0 - (1.0-smoothstep(trunkEdge, trunkEdge+0.001, abs(uvVectorFromCenter.x + 0.01f*
    sin((0.5 * mod(iTime*1.0, 10.0 * 3.14))*uvVectorFromCenter.y))) ) * 
    (1.0 - smoothstep(0.01, 0.02, uvVectorFromCenter.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}
