float Circle(vec2 uv, vec2 p, float r, float blur)
{
    float d = length(uv-p);
    float c= smoothstep(r, r-blur, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // position in 0 to 1 scale
    uv -= .5; // offset the initial position to screen center
    uv.x *= iResolution.x/iResolution.y; // display in 1:1 ratio

    float face = Circle(uv, vec2(.0), .375, .01); // draw face using a circle

    float mouth = Circle(uv, vec2(.0,-.05), .2, .005); // draw mouth using a circle
    mouth -= Circle(uv, vec2(.0,0.07), .25, .005); // erase mouth to create an eclipse
    mouth = max(mouth,.0); // prevent float value to be over 0

    float eyeL = Circle(uv, vec2(.15,.01), .05, .005); // draw an eye using a circle
    float eyeR = Circle(uv, vec2(-.15,.01), .05, .005); // draw an eye using a circle

    face -= mouth + eyeR + eyeL; // erase parts of face using mouth and eyes
    vec3 col = vec3(1.,1.0,.0)*face; // add color to face
    fragColor = vec4(col,1.0);
}
