#define sat(a) clamp(a, 0., 1.)

float _seed;
float hash11(float seed)
{
    return fract(sin(seed*123.456)*123.456);
}
float rand()
{
    return hash11(_seed++);
}
vec3 gradient(float i)
{
vec3 grad[4];
        grad[0] = vec3(0.765,0.376,0.314);
        grad[1] = vec3(0.349,0.871,0.910);
        grad[2] = vec3(0.765,0.376,0.314);
        grad[3] = vec3(0.137,0.122,0.937)*2.;
    float idx = sat(i)*3.0;
    int prevIdx = int(idx);
    int nextIdx = min(prevIdx+1, 3);
    vec3 prev = grad[prevIdx];
    vec3 next = grad[nextIdx];
    float lrp = idx - float(prevIdx);
    return mix(prev, next, smoothstep(0.,1.,lrp));
}
vec3 rdr(vec2 uv)
{
    vec3 col = vec3(0.05);

    vec2 uv2 = uv;
    float rep = .1;
       uv2.y += sin(uv2.x*3.)*.1+.2*sin(uv2.y*5.+uv.x*5.+iTime*.2);
    float lines = sin(uv2.y*280.)+.9999;

    float mask = length(uv)-.25;
    float sharp = iResolution.x*.5;
    col = mix(col, gradient(length(uv2)*2.), (1.-sat(lines))*(1.-sat(mask*sharp)));
    col = mix(col, gradient(length(-uv2)*2.).zxy, 1.-sat((abs(mask)-.001)*sharp));
    col += gradient(length(uv2)*2.)*(1.-sat(lines*.5))*.5*(1.-sat(length(uv)));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.xx;
    float stp = .007;
    uv = floor((uv/stp))*stp;
    _seed = iTime+texture(iChannel0, uv).x;
    vec3 col = rdr(uv);
    col += pow(rand(),.5)*.1;
    fragColor = vec4(col,1.0);
}
