// Based on the follow tweet: https://twitter.com/zozuar/status/1618683586928078849
// author @zozuar - yonatan

mat2 rotate2D(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 FC = fragCoord;
    vec2 r = iResolution.xy;

    float e = 0.;
    float g = 0.;
    float s = 0.;
    float u = 0.;
    vec3 col  = vec3(0);

    for(float i;i<1e2;i++){
        col += .01/exp(e*9.+vec3(1,3,4)*.1*s);
        vec3 p = vec3((FC.xy-.5*r)/r.y*g,g-6.);
        p.zx*=rotate2D(t*.5);e=s=4.;
        for(int j; j++<12;p.xy=1.-abs(p.xy+p.z*sin(t+p.z)*.1) ) {
            u=dot(p,p);
            s/=u;
            p/=-u;
            p.y=-p.y;
            p.xy=abs(p.xy+.8);
            e=min(e,p.y/s+.1/s*sin(t*.5));
        }
        g+=e;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
