// I am almost certain this can be made in under a tweet, but I can't

#define f(a) (a*3.-iResolution.xy)/iResolution.y-vec2(0.9, .5)

void mainImage( out vec4 c, in vec2 p )
{
    vec2 z = f(p),
    m = mix(sin(iTime+vec2(0, 1.6))*cos(iTime*1.6), // No mouse input
            f(iMouse.xy), // Mouse input
            min(1., iMouse.x)); // switch between the two
    for(float i = 0.; i < 50. && length(z = vec2(z.x*z.x - z.y*z.y + m.x, 2.*z.x*z.y + m.y)) < 14.; i++)
    c = cos(i/33.+vec4(5, 4, 2, 0));
}
