void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0  - iResolution.xy) / iResolution.y;
    //uv = fract(uv * 2.) - 1.5;

    float radius=sin(iTime*.1)*2.;

    vec2 uvMove = vec2(uv.x+sin(iTime*.21), uv.y+cos(iTime*.32));
    float dr = abs(sin(length(uvMove)*radius));

    radius=sin(iTime*.13)*2.;
    uvMove = vec2(uv.x+sin(iTime*.23), uv.y+cos(iTime*.39));
    float db = abs(sin(length(uvMove)*radius));

    radius=sin(iTime*.15)*2.;
    uvMove = vec2(uv.x+sin(iTime*.25), uv.y+cos(iTime*.37));
    float dg = abs(sin(length(uvMove)*radius));


    // Output to screen
    fragColor = vec4(dr, dg, db, 1.0);
}
