#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

#define DIST_MAX 1000.0

#define MAX_RAY_STEPS 64

// modified https://iquilezles.org/articles/intersectors
vec2 boxIntersection(vec3 ro, vec3 rd, vec3 s)
{
    vec3 m = 1.0 / rd;
    vec3 n = m * ro;
    vec3 k = abs(m) * s;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    if(tN > tF || tF < 0.0) return vec2(DIST_MAX);
    return vec2(tN, tF);
}

//#define HALF_GRID

// https://www.shadertoy.com/view/DtjyWD integer division - rounding down
ivec3 div_floor(ivec3 a, int b) // vector version thanks to Fabrice
{
    ivec3  S = (sign(abs(a*b))-sign(a*b))/2; // 0 if a*b >= 0
    return S * ((1 - abs(a)) / abs(b) - 1)+(1-S)*(a / b); // emulates ()?:
}

ivec3 hexagonal(ivec3 i) // group triangular coordinates to hexagonal coordinates (jt)
{
    return div_floor(i.yzx-i.zxy-1,3);
}

float height(ivec3 i)
{
    //return float(hexagonal(i).x - hexagonal(i).y);
    i = hexagonal(i);

    vec3 p = vec3(i);
    float s = 10.0;
    return 2.0*sin(2.0*pi*p.x/s) * sin(2.0*pi*p.y/s) * sin(2.0*pi*p.z/s);
}

vec3 flatten(vec3 v)
{
    vec3 n = vec3(1,1,1);
    return v - dot(v,n)*n/dot(n,n); // remove component along diagonal
}

vec4 triangle_trace(vec3 ray_pos_orig, vec3 ray_dir_orig) // step triangles by stepping through voxels along plane orthogonal to main diagonal (jt)
{
/*
    // coordinate-system along plane orthogonal to diagonal (1,1,1)
    mat3 T =
        mat3
        (
            vec3(+1.0/sqrt(2.0),-1.0/sqrt(2.0), 0.0),
            vec3(-1.0/sqrt(6.0),-1.0/sqrt(6.0), 2.0/sqrt(6.0)),
            vec3(-1.0/sqrt(3.0),-1.0/sqrt(3.0),-1.0/sqrt(3.0))
        );

    mat3 R =
        mat3 // rotate 90 degrees around (1,1,1) diagonal
        (
            vec3(1.0,1.0+sqrt(3.0),1.0-sqrt(3.0)),
            vec3(1.0-sqrt(3.0),1.0,1.0+sqrt(3.0)),
            vec3(1.0+sqrt(3.0),1.0-sqrt(3.0),1.0)
        )/3.0;
    T = R*T; // rotate 90 degrees
*/
    mat3 T =
        mat3
        (
            vec3(-1.0/sqrt(6.0),-1.0/sqrt(6.0),sqrt(2.0)/sqrt(3.0)),
            vec3(+1.0/sqrt(2.0),-1.0/sqrt(2.0),0),
            vec3(-1.0/sqrt(3.0),-1.0/sqrt(3.0),-1.0/sqrt(3.0))
        );

    // step through voxels along plane orthogonal to main diagonal (1,1,1)
    vec3 ray_pos = T * vec3(ray_pos_orig.xy, 0);
    vec3 ray_dir = T * vec3(ray_dir_orig.xy, 0);

    // initialization from https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4 implementing https://lodev.org/cgtutor/raycasting.html
    ivec3 map_pos = ivec3(floor(ray_pos));
    vec3 delta_dist = 1.0 / abs(ray_dir);
    ivec3 ray_step = ivec3(sign(ray_dir));
    vec3 side_dist = (sign(ray_dir) * (vec3(map_pos) - ray_pos) + (sign(ray_dir) * 0.5) + 0.5) * delta_dist;

    for (int i = 0; i < MAX_RAY_STEPS; i++)
    {
        // It appears to be safer to just do the box-intersection here (potential for optimization).
        vec2 bounds = boxIntersection(ray_pos - vec3(map_pos) - 0.5, ray_dir, vec3(0.5)); // bounding box
        float tmin = bounds.x; float tmax = bounds.y;
#ifdef HALF_GRID
        if(((uint(map_pos.x) ^ uint(map_pos.y) ^ uint(map_pos.z)) & 1u) == 0u)
        //if(((map_pos.x+map_pos.y+map_pos.z) & 1) == 0)
        //if((abs(map_pos.x+map_pos.y+map_pos.z)) == 1) // odd
        //if((abs(map_pos.x+map_pos.y+map_pos.z)) == 2) // even
#endif
        {
            float d = (height(map_pos)-ray_pos_orig.z)/ray_dir_orig.z;
            if(d >= max(0.0, tmin) && d <= tmax) return vec4(vec3(0,0,1),d); // hit plane
        }
#ifdef HALF_GRID
        else
#endif
        {
            bvec3 mask_next = lessThanEqual(side_dist.xyz, min(side_dist.yzx, side_dist.zxy));
            vec3 side_dist_next = side_dist + vec3(mask_next) * delta_dist;
            ivec3 map_pos_next = map_pos + ivec3(vec3(mask_next)) * ray_step;
            // prism wall?
            if(ray_pos_orig.z + ray_dir_orig.z * tmax < height(map_pos_next))
            {
                //bvec3 mask3 = lessThanEqual(side_dist2.xyz, min(side_dist2.yzx, side_dist2.zxy));
                // Calculate the normal-vector directly from the mask
                return vec4(inverse(T)*normalize(flatten((vec3(mask_next)*vec3(-ray_step)))), tmax);
                if(mask_next.z) return vec4(vec3(0,1,0),tmax);
                if(mask_next.y) return vec4(vec3(-sqrt(3.0)/2.0,-0.5,0),tmax);
                if(mask_next.x) return vec4(vec3(+sqrt(3.0)/2.0,-0.5,0),tmax);
                //if(mask_next.z) return vec4(vec3(-sin((0.0/3.0)*2.0*pi),cos((0.0/3.0)*2.0*pi),0),tmax);
                //if(mask_next.y) return vec4(vec3(-sin((1.0/3.0)*2.0*pi),cos((1.0/3.0)*2.0*pi),0),tmax);
                //if(mask_next.x) return vec4(vec3(-sin((2.0/3.0)*2.0*pi),cos((2.0/3.0)*2.0*pi),0),tmax);
                return vec4(0,0,0,tmax); // just in case...
            }
        }

        // core from https://www.shadertoy.com/view/4dX3zl Branchless Voxel Raycasting by fb39ca4 implementing https://lodev.org/cgtutor/raycasting.html
        bvec3 mask = lessThanEqual(side_dist.xyz, min(side_dist.yzx, side_dist.zxy)); // fb39ca4: "Thanks kzy for the suggestion!"
        side_dist += vec3(mask) * delta_dist;
        map_pos += ivec3(vec3(mask)) * ray_step;
    }

    return vec4(-ray_dir_orig, DIST_MAX); // sky-sphere
}

void mainImage( out vec4 fragColor, in vec2 I )
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    //I = 2.0 * I.xy / R - 1.0;
    //I.x *= R.x / R.y;
    I = (2.0 * I - R) / R.y; // concise pixel-position mapping thanks to Fabrice
    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 1.25 * pi/2.0;

    vec3 ray_pos = vec3(0.0, 0.0, -12.0);
    vec3 ray_dir = vec3(I.x, I.y, 2.0); // NOTE: un-normalized direction appears to work fine here!
    //ray_dir = normalize(ray_dir); // looks like ray-direction does not need to be normalized

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ray_pos = M * ray_pos;
    ray_dir = M * ray_dir;
    ray_pos.z += 0.5;

    vec3 color = vec3(0);
    vec4 result = triangle_trace(ray_pos, ray_dir);
    if(result.w < DIST_MAX)
    {
        color = vec3(1);
        if(false)
        {
            //vec3 light_dir = normalize(vec3(1,2,3));
            vec3 light_dir = normalize(vec3(cos(iTime),sin(iTime),1));
            float diffuse = max(0.0, dot(result.xyz, light_dir));
            color *= diffuse;
        }
        else
        {
            vec3 dst = ray_pos + ray_dir * result.w;
            //vec3 light_pos = vec3(0,0,2);
            vec3 light_pos = vec3(3.0*vec2(cos(iTime),sin(iTime)),2);
            vec3 light_diff = light_pos - dst;
            float light_dist = length(light_diff);
            vec3 light_dir = light_diff / light_dist;
            vec3 light_color = vec3(1) * 5.0;
            vec3 diffuse = light_color * max(0.0, dot(result.xyz, light_dir)) / (light_dist * light_dist);
            float ambient = 0.005;
            color = (diffuse + ambient) * (0.5 + 0.5 * result.xyz); // visualize normals
            //color = vec3(fract(result.w));
        }
        //color = 0.5 +0.5*result.xyz;
    }
    color = tanh(color); // soft-clamp overly bright colors
    fragColor = vec4(sqrt(color), 1.0); // gamma
}
