float plotFunction(float x) {
    /*float frequency = 500.;
    float amplitude = .5;
    return amplitude * tan(frequency * 3.14159265 * sin(x) * tan(iTime / 10.) * x);*/
    float frequency = 1500.0;
    float amplitude = .5;
    return amplitude * sin(frequency * 3.14159265 * x * tan(x) * (x / 13.) + iTime);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.y = uv.y * iResolution.y / iResolution.x;


    float functionY =  plotFunction(uv.x);

    float thickness = 0.1;

    if (abs(uv.y - functionY) < thickness) {
        vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col, 1.0);
    } else {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
