void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = 0.5 + uv.x-uv.y*exp(fract(iTime+uv.xyx+vec3(iTime)));
    vec2 pa = col.yz/uv*exp(col.xy*cos(iTime));
    vec4 a = texture(iChannel0,pa-uv);
    // Output to screen
    fragColor = vec4(a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = fragCoord.x;
    float y = fragCoord.y;
    vec2 b = vec2(sin(x-y)/exp(34./iTime));
    vec2 a = exp(b*fragCoord);
    fragColor = vec4(b,a);
}
