void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // random data points
    vec2 dataPoints[5];
    dataPoints[0] = vec2(0.2, 0.4);
    dataPoints[1] = vec2(0.4, 0.6);
    dataPoints[2] = vec2(0.6, 0.8);
    dataPoints[3] = vec2(0.8, 0.2);
    dataPoints[4] = vec2(0.9, 0.5);


    // Chart settings
    float pointSize = 0.01;  // Size of the scatter plot points
    float barWidth = 0.02;   // Width of the bar chart bars

    // Scatter plot
    for (int i = 0; i < 5; i++) {
        vec2 point = dataPoints[i];
        vec2 distance = abs(uv - point);
        if (distance.x < pointSize && distance.y < pointSize) {
            // Set the color of the scatter plot point
            fragColor = vec4(1.0, 0.0, 0.0, 1.0); // Example: red color
            return;
        }
    }

    // Bar chart
    for (int i = 0; i < 5; i++) {
        vec2 point = dataPoints[i];
        if (uv.x > point.x - barWidth / 2.0 && uv.x < point.x + barWidth / 2.0 && uv.y < point.y) {
            // Set the color of the bar chart bar
            fragColor = vec4(0.0, 0.0, 1.0, 1.0); // Example: blue color
            return;
        }
    }

    // Generate a color based on the UV coordinates or data values
    vec3 color = vec3(uv.x, uv.y, 0.5); // Example: using UV coordinates

    // Modify the color based on your data values or logic
    // Example: color = vec3(dataValue, 0.0, 0.0); // Using a single data value for red component

    // Set the alpha channel to 1.0 (fully opaque)
    fragColor = vec4(color, 0.5);
}
