#define NUM_POINTS 5

float timeElapsed;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Ajusta la velocidad de animación
    float speed = 0.1;

    // Actualiza el tiempo transcurrido
    timeElapsed += iTime * speed;

    // Inicializar el color de fondo
    vec3 backgroundColor = vec3(0.0, 0.0, 0.0);
    fragColor = vec4(backgroundColor, 1.0);

    // Dibujar puntos y líneas
    for (int i = 0; i < NUM_POINTS; i++) {
        // Calcula la posición del punto
        vec2 point = vec2(
            mod(sin(timeElapsed + float(i)), 1.0) * iResolution.x,
            mod(cos(timeElapsed + float(i)), 1.0) * iResolution.y
        );

        // Dibuja el punto
        float distance = length(fragCoord - point);
        if (distance < 5.0) {
            fragColor = vec4(1.0, 1.0, 1.0, 1.0); // Color de los puntos
        }

        // Dibuja líneas
        for (int j = 0; j < NUM_POINTS; j++) {
            if (i != j) {
                vec2 otherPoint = vec2(
                    mod(sin(timeElapsed + float(j)), 1.0) * iResolution.x,
                    mod(cos(timeElapsed + float(j)), 1.0) * iResolution.y
                );

                vec2 dir = normalize(otherPoint - point);
                float len = length(otherPoint - point);

                for (float t = 0.0; t < 1.0; t += 0.01) {
                    vec2 pos = point + t * len * dir;
                    float distance = length(fragCoord - pos);
                    if (distance < 1.0) {
                        fragColor = vec4(1.0, 1.0, 1.0, 1.0); // Color de las líneas
                    }
                }
            }
        }
    }
}
